.App {
  text-align: center;
}

.album-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
}

.album-thumbnail {
  border: 1px solid #ccc;
  padding: 10px;
}

.thumbnail {
  width: 100px;
  height: 100px;
  object-fit: cover;
  cursor: pointer;
}

.album-view {
  position: fixed;
  top: 10%;
  left: 10%;
  right: 10%;
  background: white;
  padding: 20px;
  border: 1px solid #ccc;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
}

.album-images img {
  max-width: 100%;
  height: auto;
}
