import React, { useState, useCallback } from 'react';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import { useDropzone } from 'react-dropzone';

function AlbumForm({ addAlbum }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [images, setImages] = useState([]);

  const onDrop = useCallback((acceptedFiles) => {
    setImages([...images, ...acceptedFiles]);
  }, [images]);

  const { getRootProps, getInputProps } = useDropzone({ onDrop });

  const handleSubmit = (event) => {
    event.preventDefault();
    if (title && description && images.length > 0) {
      const newAlbum = { title, description, images };
      addAlbum(newAlbum);
      setTitle('');
      setDescription('');
      setImages([]);
    } else {
      alert('Please fill in all fields and upload images.');
    }
  };

  return (
    <>
      <div className='container'>
        <form onSubmit={handleSubmit}>
          <h2><u>Add Photo</u></h2>

          <div className="mb-3">
            <input type="text" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} placeholder='Image Title' />
          </div>

          {/* <div className="mb-3"> */}
            <label>Images:</label>
            <div {...getRootProps({ className: 'dropzone' })} style={dropzoneStyle}>
              <input {...getInputProps()} />
              <p>Drag 'n' drop some files here, or click to select files</p>
            </div>
            <div className="mt-2">
              {images.length > 0 && (
                <div>
                  <h4>Selected Images:</h4>
                  <ul>
                    {images.map((file, index) => (
                      <li key={index}>{file.name}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>

          <div className="mb-3" style={{ width: '400px' }}>
            <CKEditor className="CKEditor"
              editor={ClassicEditor}
              data={description}
              config={{
                placeholder: 'Enter your description here...'
              }}
              onChange={(event, editor) => {
                const data = editor.getData();
                setDescription(data);
              }}
            />
          </div>
          
          <button className="btn btn-success" type="submit">Create Album</button>
        </form>
      </div>
    </>
  );
}

const dropzoneStyle = {
  border: '2px dashed #cccccc',
  borderRadius: '4px',
  padding: '20px',
  textAlign: 'center',
  cursor: 'pointer',
  backgroundColor: '#fafafa',
};

export default AlbumForm;
